      arr2.push(true)
    }
  }


  // console.log(arr)

  return (
    <div className="App">
      {arr.map((el) => (
        <Line
          key={el}
          number={el}
          state={el % 2 == 0 ? true : false} // попробуй сделать duble for
        />)
      )}
    </div>
  );
}

